     */
    function edit($inv_id)
    {   
        // check if the inv_detail exists before trying to edit it
        $data['inv_detail'] = $this->Inv_detail_model->get_inv_detail($inv_id);
        
        if(isset($data['inv_detail']['inv_id']))
        {
