					'redirect_url' => $this->input->post('redirect_url'),
					'code_gen_date' => $this->input->post('code_gen_date'),
					'token_gen_dt' => $this->input->post('token_gen_dt'),
					'status' => $this->input->post('status'),
                );

                $this->Inv_detail_model->update_inv_detail($inv_id,$params);            
                redirect('inv_detail/index');
            }
            else
