    /*
     * Deleting inv_detail
     */
    function remove($inv_id)
    {
        $inv_detail = $this->Inv_detail_model->get_inv_detail($inv_id);

        // check if the inv_detail exists before trying to delete it
        if(isset($inv_detail['inv_id']))
        {
            $this->Inv_detail_model->delete_inv_detail($inv_id);
            redirect('inv_detail/index');
